var s = {
    config: {
        ship: {
            hull: 120,
            shields: 80,
            maxSpeed: 1500
        },
        sound: {
            enabled: true,
            silentDistance: 10000,
            sounds: {
                laser: "/game/sounds/laser.mp3"
            }
        }
    },

    init: function() {
        console.log('Satellite starting...');
        // Create a projector for 2D <-> 3D calculations
        s.projector = new THREE.Projector();

        // Create a model loader
        s.loader = new THREE.JSONLoader();

        // Create game
        s.game = new s.SatelliteGame();
    }
};

s.util = {};

Math.toDegrees = function(radians) {
	return radians * 180/Math.PI;
};

Math.toRadians = function(degrees) {
	return degrees * Math.PI/180;
};

/**
	Load textures
	
	@param {Object} options  Options object
	@param {Array} models  Array of texture names to load
	@param {Function} complete  Function to call when loading is complete
	@param {Function} progress  Function to call when model is loaded
*/
s.util.loadTextures = function(options) {
	var toLoad = options.textures.length;

	// Hold textures
	var textures = {};

	var loaded = function(name, texture) {
		// Store texture
		textures[name] = texture;
		
		// Track progress
		toLoad--;
		if (toLoad === 0) {
			console.log('Textures loaded!');
			if (typeof options.complete === 'function')
				options.complete(textures);
		}
		else {
			var pct = (options.textures.length-toLoad)/options.textures.length*100;

			console.log('Loading textures: '+pct.toFixed(0)+'%');
			if (typeof options.progress === 'function')
				options.progress(pct, textures[name]);
		}
	};
	
	options.textures.forEach(function(name, index) {
		// Strip file extension
		var shortName = name.split('.')[0];

		new THREE.ImageUtils.loadTexture('game/textures/'+name, {}, loaded.bind(null, shortName));
	});
};

/**
	Load models
	
	@param {Object} options  Options object
	@param {Array} models  Array of model names to load
	@param {Function} complete  Function to call when loading is complete
	@param {Function} progress  Function to call when model is loaded
*/
s.util.loadModels = function(options) {
	var toLoad = options.models.length;

	// Hold models
	var models = {};

	var loaded = function(name, geometry, materials) {
		// Store model and materials
		models[name] = {
			geometry: geometry,
			materials: materials
		};
		
		// Track progress
		toLoad--;
		if (toLoad === 0) {
			console.log('Models loaded!');
			if (typeof options.complete === 'function')
				options.complete(models);
		}
		else {
			var pct = (options.models.length-toLoad)/options.models.length*100;

			console.log('Loading models: '+pct.toFixed(0)+'%');
			if (typeof options.progress === 'function')
				options.progress(pct, models[name]);
		}
	};
	
	options.models.forEach(function(name, index) {
		s.loader.load('game/models/'+name+'.json', loaded.bind(null, name));
	});
};

/**
	Get 3D coordinates of a 2D point
	
	@param {Number} x  2D x coordinate
	@param {Number} y  2D y coordinate
	@param {Number} width  Width of canvas
	@param {Number} height  Height of canvas
	@param {THREE.PerspectiveCamera} camera  three.js camera instance
*/
s.util.get3DCoords = function(x, y, width, height, camera) {
	// Convert to normalized device coordinates
	var ndc = s.util.getNDC(x, y, width, height);
	
	var startVector = new THREE.Vector3();
	var endVector = new THREE.Vector3();
	var dirVector = new THREE.Vector3();
	var goalVector = new THREE.Vector3();
	var t;
	
	// Create vectors above and below ground plane at our NDCs
	startVector.set(ndc.x, ndc.y, -1.0);
	endVector.set(ndc.x, ndc.y, 1.0);
	
	// Convert back to 3D world coordinates
	startVector = s.projector.unprojectVector(startVector, camera);
	endVector = s.projector.unprojectVector(endVector, camera);
	
	// Get direction from startVector to endVector
	dirVector.subVectors(endVector, startVector);
	dirVector.normalize();
	
	// Find intersection where y = 0
	t = startVector.y / -(dirVector.y);

	// Find goal point
	goalVector.set(
		startVector.x + t * dirVector.x,
		startVector.y + t * dirVector.y,
		startVector.z + t * dirVector.z
	);
	
	return goalVector;
};

/**
	Create a ray between two points
	
	@param {THREE.Vector3} pointA  Start point
	@param {THREE.Vector3} pointB  End point
*/
s.util.createRay = function(pointA, pointB) {
	var rayStart = pointA;
	var rayDirection = new THREE.Vector3();
	rayDirection.subVectors(pointB, pointA).normalize();
	
	return new THREE.Raycaster(rayStart, rayDirection);
};

/**
	Get the normalized device coordinate 
	
	@param {Number} x  2D x coordinate
	@param {Number} y  2D y coordinate
	@param {Number} width  Width of canvas
	@param {Number} height  Height of canvas
*/
s.util.getNDC = function(x, y, width, height) {
	return {
		x: (x / width) * 2 - 1,
		y: -(y / height) * 2 + 1
	};
};

/**
	Get 2D coordinates from a Vector3
	
	@param {THREE.Vector3} objVector  Vector representing the object position
	@param {Number} width  Width of canvas
	@param {Number} height  Height of canvas
*/
s.util.get2DCoords = function(objVector, width, height) {
	var vector3D = objVector.clone();
	
	var vector2D = this.projector.projectVector(vector3D, this.camera);
	
	vector2D.y = -(vector2D.y*height - height)/2;
	vector2D.x = (vector2D.x*width + width)/2;
	
	return vector2D;
};

s.util.debounce = function(func, wait, immediate) {
    var result;
    var timeout = null;
    return function() {
      var context = this, args = arguments;
      var later = function() {
        timeout = null;
        if (!immediate) result = func.apply(context, args);
      };
      var callNow = immediate && !timeout;
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
      if (callNow) result = func.apply(context, args);
      return result;
    };
};

s.util.largerThan = function(vector, value){
	if(Math.abs(vector.x) > value || Math.abs(vector.y) > value || Math.abs(vector.z) > value) return true;
	return false;
};

/**
 * Provides observer pattern for basic eventing
 *
 * @class	
 */
s.EventEmitter = new Class({
	/** @constructs */
	construct: function() {
		this._events = {};
	},
	
	/**
	 * Destroy references to events and listeners.
	 */
	destruct: function() {
		delete this._events;
	},
	
	/**
	 * Attach an event listener
	 *
	 * @param {String} evt		Name of event to listen to
	 * @param {Function} func	Function to execute
	 *
	 * @returns {EventEmitter}	this, chainable
	 */
	on: function(evt, func) {
		var listeners = this._events[evt] = this._events[evt] || [];
		listeners.push(func);
		
		return this;
	},

	/**
	 * Remove an event listener
	 *
	 * @param {String} evt		Name of event that function is bound to
	 * @param {Function} func	Bound function
	 *
	 * @returns {EventEmitter}	this, chainable
	 */
	off: function(evt, func) {
		var listeners = this._events[evt];
		if (listeners !== undefined);
			listeners.splice(listeners.indexOf(func), 1);
		
		return this;
	},
	
	/**
	 * Trigger an event
	 *
	 * @param {String} evt		Name of event to trigger
	 * @param {Arguments} args	Additional arguments are passed to the listener functions
	 *
	 * @returns {EventEmitter}	this, chainable
	 */
	trigger: function(evt) {
		var listeners = this._events[evt];
		if (listeners !== undefined) {
			for (var i = 0, n = listeners.length; i < n; i++) {
				listeners[i].apply(this, Array.prototype.slice.call(arguments, 1));
			}
		}
		
		return this;
	}
});

s.GameObject = new Class({
	toString: 'GameObject',
	extend: s.EventEmitter,

	construct: function(options) {
		// Bind execution scope of update, if necessary
		if (this.update)
			this.update = this.update.bind(this);

		// Store scene for remove
		this.game = options.game;
	},

	init: function() {
		// Store a reference to the instance on the object
		// This is used after a collision is detected
		// For instance, to remove HP from the item hit
		if (this.root)
			this.root.instance = this;

		this.add();
	},

	destruct: function() {
		// Unhook from the rendering loop
		if (this.update)
			this.game.unhook(this.update);

		// Remove from the scene
		if (this.root)
			this.game.scene.remove(this.root);
	},

	add: function() {
		// Add mesh to world
		if (this.root)
			this.game.scene.add(this.root);

		// Hook to the rendering loop
		if (this.update && !this.hooked) {
			this.game.hook(this.update);
			this.hooked = true;
		}

		return this;
	},

	getRoot: function() {
		return this.root;
	},

	show: function() {
		this.root.visible = true;
	},

	hide: function() {
		this.root.visible = false;
	}
});

s.Shield = new Class({
	extend: s.GameObject,

	construct: function(options){
        this.ship = options.ship;
        var geometry = new THREE.SphereGeometry(50,50,50);
        var material = new THREE.MeshLambertMaterial({
            color: 0x00F2FF,
            ambient: 0x00F2FF,
            transparent: true,
            blending: THREE.AdditiveBlending,
            opacity: 0.5
        });
        this.mesh = new THREE.Mesh(geometry, material);
        this.ship.add(this.mesh);
        this.startTime = null;
	},

    update: function(){
        if(this.startTime === null){
            this.startTime = new Date().getTime();
        }

        var progress = new Date().getTime() - this.startTime;

        if(progress > 1000){
            this.destruct();
            this.ship.remove(this.root);
        }

        if(this.mesh.material.opacity > 0){
            this.mesh.material.opacity -= 0.04;
        }
    }
});

s.Sound = new Class({
	toString: 'Sound',
	
	construct: function(options) {
		this.enabled = options.enabled !== undefined ? options.enabled : true;

		this.sounds = options.sounds || {};
		
		this.ready = {};
		
		this.preload();
	},
	
	destruct: function() {
		// TODO: Remove event listeners from Audio objects?
		this.ready = {};
	},
	
	preload: function() {
		// Preload each sound
		for (var name in this.sounds) {
			var soundUrl = this.sounds[name];
			
			// Create array to hold Audio instances
			this.ready[soundUrl] = [];
			
			// Create an instance of each sound
			this.ready[soundUrl].push(this.create(soundUrl));
		}
	},
	
	create: function(soundUrl) {
		// Create a new Audio object with the URL provided
		var soundObj = new Audio(soundUrl);
		
		var that = this;
		
		// When the Audio is done playing, add it back to the ready array
		soundObj.addEventListener('ended', function() {
			that.ready[soundUrl].push(soundObj);
		});
		
		return soundObj;
	},
	
	getInstance: function(name) {
		// Check if we have the requested sound
		var soundUrl = this.sounds[name];
		if (!soundUrl) {
			console.error('Bug: invalid sound requested');
			return false;
		}
		
		// Create ready array, if necessary. Only required if preload() isn't called
		if (!this.ready[soundUrl])
			this.ready[soundUrl] = [];
		
		// Check for an already created Audio instance to use
		var soundObj;
		if (this.ready[soundUrl].length) {
			// Pull the Audio instance from the ready array
			soundObj = this.ready[soundUrl].shift();
		}
		else {
			// Create a new Audio instance
			soundObj = this.create(soundUrl);
		}
		
		return soundObj;
	},
	
	play: function(name, volume) {
		if (!this.enabled)
			return false;
		
		var soundObj = this.getInstance(name);
		soundObj.volume = volume || 1;

		// Play the sound
		if (soundObj)
			soundObj.play();
	}
});
s.Explosion = new Class({
	extend: s.GameObject,

	construct: function(options){
		var pGeometry = new THREE.Geometry();
			for(var i = 0; i < 10; i++){
				var vertex = new THREE.Vector3();
				vertex.x = Math.random() * 20 - 10;
				vertex.y = Math.random() * 20 - 10;
				vertex.z = Math.random() * 20 - 10;
				pGeometry.vertices.push(vertex);
			}

		var pMaterial = new THREE.ParticleBasicMaterial({
			color: 0xFFFFFF,
			size: 300,
			map: s.textures.explosion,
			blending: THREE.AdditiveBlending,
			transparent: true
		});

		this.root = new THREE.ParticleSystem(pGeometry, pMaterial);
		this.root.sortParticles = true;
		this.root.position.copy(options.position);

		this.startTime = null;
		this.animationTime = 1000;
	},

	update: function(){
		if(this.startTime === null){
			this.startTime = new Date().getTime();
		}

		var progress = new Date().getTime() - this.startTime;
		var proportionalProgress = progress/this.animationTime;
		var scale = 8 * proportionalProgress;
		this.root.scale.set(scale,scale,scale);
		this.root.material.opacity = 1 - proportionalProgress;

		if(progress > this.animationTime){
			this.hide();
		}
	}
});

s.Projectile = new Class({
	extend: s.GameObject,

    construct: function(options){
        this.HUD = options.HUD || null;
        this.game = options.game;
        this.comm = this.game.comm;
        this.game = options.game;
        this.pilot = options.pilot;
        this.bot = options.bot;
        // handle parameters
        this.initialVelocity = options.initialVelocity;
        var that = this;
        // Destory projectile after 4 secs
        setTimeout(function(){
            that.destruct();
        }, 4000);
    },

	init: function(_super){
        _super.call(this);
        this.applyForce();
    },

    handleCollision: function(mesh, position){
        //check if your turret hit someone
        //else if check if you got hit by a bot
        if (this.pilot === this.game.pilot.name){
            if (mesh.instance.alliance && mesh.instance.alliance === "enemy"){
                this.HUD.menu.animate({
                color: this.HUD.hit,
                frames: 30
                });
                if(mesh.instance.shields > 0){
                    new s.Shield({
                        game: this.game,
                        ship: mesh
                    });
                }
                this.comm.hit(mesh.name,this.game.pilot.name);
            }
        } else if (mesh.name === this.game.pilot.name && this.bot ) {
            this.comm.botHit(mesh.name,this.game.pilot.name);
        }
        this.destruct();
    },

    applyForce: function() {
        // Take on the initial velocity
        this.root.setLinearVelocity(this.initialVelocity);

        // Apply an impulse to move forward
        this.root.applyCentralImpulse(this.getForceVector());
    },

    getForceVector: function(){
        // Make sure the projectile's matrix is up to date
        this.root.updateMatrix();

        // Extract the rotation from the projectile's matrix
        this.rotationMatrix = new THREE.Matrix4();
        this.rotationMatrix.extractRotation(this.root.matrix);

        // Apply bullet impulse in the correct direction
        return new THREE.Vector3(0, 0, this.options.velocity * -1).applyMatrix4(this.rotationMatrix);
    }
});

s.Turret = new Class({
    toString: 'Turret',

	extend: s.Projectile,

    options: {
        mass: 1,
        size: 25,
        velocity: 5000,
        damage: 1000,
        scale: new THREE.Vector3(50, 50, 1.0),
        color: {
            alliance: 0x00F2FF,
            rebels: 0xFF0000
        }
    },

	construct: function(options){
        // Handle parameters
        this.color = this.options.color[options.team];

        var geometry = new THREE.SphereGeometry(this.options.size);
        var material = Physijs.createMaterial(new THREE.MeshBasicMaterial({visible: false}));
        this.root = new Physijs.SphereMesh(geometry, material, this.options.mass);
        this.root.addEventListener('collision', this.handleCollision.bind(this));

        // Draw the projectile to the screen
        var sprite = new THREE.Sprite(new THREE.SpriteMaterial({
            map: s.textures.particle,
            useScreenCoordinates: false,
            blending: THREE.AdditiveBlending,
            color: this.color
        }));

        sprite.scale.copy(this.options.scale);
        this.root.add(sprite);

        // Position the projectile relative to the ship
        this.root.position.copy(options.position);
        this.root.rotation.copy(options.rotation);
	}
});

s.Missile = new Class({
    toString: 'Missile',

    extend: s.Projectile,

    options: {
        mass: 1,
        color: {
            alliance: 0x00F2FF,
            rebels: 0xFF0000
        },
        damage: 500,
        velocity: 1000
    },

    construct: function(options) {
        // handle parameters
        this.color = this.options.color[options.team];
        this.velocity = this.options.velocity;

        // Create a cylinder
        var geometry = new THREE.CylinderGeometry(3,3,40);
        var material = new THREE.MeshBasicMaterial({ color: this.color });

        // Rotate the cylinder to face the Z direction
        geometry.applyMatrix(new THREE.Matrix4().makeRotationX(Math.PI/2));

        // Create the mesh
        this.root = new Physijs.CylinderMesh(geometry, material, this.options.mass);

        // Position the projectile relative to the ship
        this.root.position.copy(options.position);
        this.root.rotation.copy(options.rotation);
    }
});

s.Ship = new Class({
	extend: s.GameObject,

    options: {
        leftTurretOffset: new THREE.Vector3(35, 0, -200),
        rightTurretOffset: new THREE.Vector3(-35, 0, -200),
        missileOffset: new THREE.Vector3(0, 0, -120),
        turretFireTime: 200,
        botTurretFireTime: 3000,
        missileFireTime: 1000
    },

    botOptions: {
        rotationSpeed: Math.PI/2,
        pitchSpeed: Math.PI/4,
        yawSpeed: Math.PI/4,
        thrustImpulse: 0,
        brakePower: 0.85,
        velocityFadeFactor: 16,
        rotationFadeFactor: 4
    },

	construct: function(options) {

        this.HUD = options.HUD;
		var geometry = s.models[options.shipClass].geometry;
		this.materials = s.models[options.shipClass].materials[0];
        this.materials.emissive = new THREE.Color('rgb(255,255,255)');

        var physiMaterial = Physijs.createMaterial(this.materials);
		this.root = new Physijs.ConvexMesh(geometry, physiMaterial, 100);
		this.root.position.copy(options.position);
		this.root.rotation.copy(options.rotation);

        this.lastTurretFire = 0;
        this.lastMissileFire = 0;
        this.alliance = options.alliance;

        this.game = options.game;
        this.bot = options.bot || false;
        this.name = options.name || '';

        this.root.name = this.name;
        this.hull = s.config.ship.hull;
        this.shields = s.config.ship.shields;

        //////////////////////////////
        //////      BOT LOGIC    /////
        //////////////////////////////

        if (options.bot) {
            //bot initialization
            this.controlBot = this.controlBot.bind(this);
            this.targetX = 0;
            this.targetY = 0;

            
            //Create a camera for the bot
            this.camera = new THREE.PerspectiveCamera(35, 1, 1, 300000);

             // Root camera to the bot's position
            this.root.add( this.camera );

            // Setup camera: Cockpit view; COMMENT OUT FOR CHASE CAM
            this.camera.position.set( 0, 0, 0 );

            //set a hook on the bot controls.
            //necessary because first player has bot join twice
            if (this.game.lastBotCallback) { this.game.unhook( this.game.lastBotCallback ); }
            this.game.hook( this.controlBot );
            this.game.lastBotCallback = this.controlBot;
        }

        this.lastTime = new Date( ).getTime( );

	},

    getOffset: function(offset) {
        return offset.clone().applyMatrix4(this.root.matrixWorld);
    },

	fire: function(weapon){
		var now =new Date().getTime();
        var position;
        var rotation = this.root.rotation.clone();
        var initialVelocity = this.root.getLinearVelocity().clone();

        // Turrets
        if (weapon === 'turret'){
            if (now - this.lastTurretFire > this.options.turretFireTime){
                // Left bullet
                position = this.getOffset(this.options.leftTurretOffset);
                new s.Turret({
                    HUD: this.HUD,
                    game: this.game,
                    pilot: this.name,
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity,
                    team: this.alliance
                });
                this.game.handleFire({
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity
                });

                // Right bullet
                position = this.getOffset(this.options.rightTurretOffset);
                new s.Turret({
                    HUD: this.HUD,
                    game: this.game,
                    pilot: this.game.pilot.name,
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity,
                    team: this.alliance
                });
                this.game.handleFire({
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity
                });

                this.lastTurretFire = now;

                this.game.sound.play('laser', 0.5);
            }
        }

        // Missiles
        if (weapon === 'missile'){
            if(now - this.lastMissileFire > this.options.missileFireTime){
                position = this.getOffset(this.options.missileOffset);

                new s.Missile({
                    game: this.game,
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity,
                    team: this.alliance
                });

                this.lastMissileFire = now;
            }
        }
    },

    botFire: function (weapon) {
        var now =new Date().getTime();
        var position;
        var rotation = this.root.rotation.clone();
        var initialVelocity = this.root.getLinearVelocity().clone();

        // Turrets
        if (weapon === 'turret'){
            if (now - this.lastTurretFire > this.options.botTurretFireTime){
                // Left bullet
                position = this.getOffset(this.options.leftTurretOffset);
                var bulletLeft = new s.Turret({
                    game: this.game,
                    pilot: this.name,
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity,
                    bot: this.bot,
                    team: this.alliance
                });

                // Right bullet
                position = this.getOffset(this.options.rightTurretOffset);
                var bulletRight = new s.Turret({
                    game: this.game,
                    pilot: this.name,
                    position: position,
                    rotation: rotation,
                    initialVelocity: initialVelocity,
                    bot: this.bot,
                    team: this.alliance
                });

                this.lastTurretFire = now;

                if (!this.bot) {
                    this.game.sound.play('laser', 0.5);
                }
            }
        }
    },

    setPosition: function (position, rotation, aVeloc, lVeloc, interpolate) {
        var posInterpolation = 0.05;
        var rotInterpolation = 0.50;

        if (interpolate) {
            // Interpolate position by adding the difference of the calculated position and the position sent by the authoritative client
            var newPositionVec = new THREE.Vector3(position[0], position[1], position[2]);
            var posErrorVec = newPositionVec.sub(this.root.position).multiply(new THREE.Vector3(posInterpolation, posInterpolation, posInterpolation));
            this.root.position.add(posErrorVec);
        }
        else {
            // Directly set position
            this.root.position.set(position[0], position[1], position[2]);
        }

        // Set rotation
        if (rotation)
            this.root.rotation.set(rotation[0], rotation[1], rotation[2]);

        if (aVeloc !== undefined && this.root.setAngularVelocity)
            this.root.setAngularVelocity({ x: aVeloc[0], y: aVeloc[1], z: aVeloc[2] });

        if (lVeloc !== undefined && this.root.setLinearVelocity)
            this.root.setLinearVelocity({ x: lVeloc[0], y: lVeloc[1], z: lVeloc[2] });

        // Tell the physics engine to update our position
        this.root.__dirtyPosition = true;
        this.root.__dirtyRotation = true;
    },


    getPositionPacket: function() {
        var root = this.getRoot();

        // Position & rotation
        var shipPosition = (root && root.position) || new THREE.Vector3();
        var shipRotation = (root && root.rotation) || new THREE.Vector3();

        // Velocity
        var linearVelocity = (root.getLinearVelocity && root.getLinearVelocity()) || new THREE.Vector3();
        var angularVelocity = (root.getAngularVelocity && root.getAngularVelocity()) || new THREE.Vector3();

        return {
            pos: [shipPosition.x, shipPosition.y, shipPosition.z],
            rot: [shipRotation.x, shipRotation.y, shipRotation.z],
            aVeloc: [angularVelocity.x, angularVelocity.y, angularVelocity.z],
            lVeloc: [linearVelocity.x, linearVelocity.y, linearVelocity.z]
        };
    },

    getForceVector: function(){

        // Extract the rotation from the projectile's matrix
        var rotationMatrix = new THREE.Matrix4();
        rotationMatrix.extractRotation(this.root.matrix);

        // Apply bullet impulse in the correct direction
        return new THREE.Vector3(0, 0, 1500 * -1).applyMatrix4(rotationMatrix);
    },

    handleDie: function(){
        this.destruct();
    },

    controlBot: function( ) {


        //////////////////////////////
        ////  CLOSEST ENEMY LOGIC ////
        //////////////////////////////  

        //MAKE ENEMY LIST FOR BOT
        var botEnemyList = [];
        botEnemyList.push(this.game.player);
        var enemyShips = this.game.enemies._list;
        for (var i = 0; i < enemyShips.length; i++) {
            if (!enemyShips[i].bot) {
                botEnemyList.push(enemyShips[i]);
            }
        }

        //DETERMINE CLOSEST ENEMY
        var closestDistance;
        for (i = 0; i < botEnemyList.length; i++) {
            var distance = this.root.position.distanceTo(botEnemyList[i].root.position);
            if (!closestDistance || distance < closestDistance) {
                closestDistance = distance;
                this.target = botEnemyList[i];
            }
        }

        //////////////////////////////
        //// THRUST/BREAK LOGIC ////
        //////////////////////////////    

        var now = new Date( ).getTime( );
        var difference = now - this.lastTime;

        var thrust = 0;
        var brakes = 0;

        var  maxDistance = 4100, minDistance = 1500;

        if (closestDistance > maxDistance) {
            thrust = 1;
        }
        else if (closestDistance < minDistance) {
            brakes = 1;
        } else {
            var ratio = (closestDistance - minDistance) / (maxDistance - minDistance);
            var optimumSpeed = s.config.ship.maxSpeed * ratio;
            if (optimumSpeed < this.botOptions.thrustImpulse) { brakes = 1; }
            if (optimumSpeed > this.botOptions.thrustImpulse) { thrust = 1; }
        }

        if (thrust && this.botOptions.thrustImpulse < s.config.ship.maxSpeed){
            this.botOptions.thrustImpulse += difference;
        }

        if (brakes && this.botOptions.thrustImpulse > 0){
            this.botOptions.thrustImpulse -= difference;
        }


        //////////////////////////////
        // LEFT/RIGHT/UP/DOWN LOGIC //
        //////////////////////////////       

        var vTarget3D;
        var vTarget2D;

        var pitch = 0;
        var roll = 0;
        var yaw = 0;

        var yawSpeed    = this.botOptions.yawSpeed,
            pitchSpeed  = this.botOptions.pitchSpeed;

        var thrustScalar = this.botOptions.thrustImpulse/s.config.ship.maxSpeed + 1;

        // TARGET HUD MARKING
        if ( this.target ) {
            this.target = this.target.root;

            vTarget3D = this.target.position.clone();
            vTarget2D = s.projector.projectVector(vTarget3D, this.camera);
        }

        if (vTarget2D.z < 1) {
            //go left; else if go right
            if (vTarget2D.x < -0.15) {
                yaw = yawSpeed / thrustScalar;
            } else if (vTarget2D.x > 0.15) {
                yaw = -1 * yawSpeed / thrustScalar;
            }
            //do down; else if go up
            if (vTarget2D.y < -0.15) {
                pitch = -1*pitchSpeed / thrustScalar;
            } else if (vTarget2D.y > 0.15) {
                pitch  = pitchSpeed / thrustScalar;
            }
        } else {
            //go right; else if go left
            if (vTarget2D.x < 0) {
                yaw = -1* yawSpeed / thrustScalar;
            } else if (vTarget2D.x >= 0) {
                yaw = yawSpeed / thrustScalar;
            }
            //do up; else if go down
            if (vTarget2D.y < 0) {
                pitch = pitchSpeed / thrustScalar;
            } else if (vTarget2D.y > 0) {
                pitch  = -1 * pitchSpeed / thrustScalar;
            }
        }
            
        //////////////////////////////
        // MOTION AND PHYSICS LOGIC //
        //////////////////////////////


        var linearVelocity = this.root.getLinearVelocity().clone();
        var angularVelocity = this.root.getAngularVelocity().clone();
        var rotationMatrix = new THREE.Matrix4();
        rotationMatrix.extractRotation(this.root.matrix);

        angularVelocity = angularVelocity.clone().divideScalar(this.botOptions.rotationFadeFactor);
        this.root.setAngularVelocity(angularVelocity);

        var newAngularVelocity = new THREE.Vector3(pitch, yaw, roll).applyMatrix4(rotationMatrix).add(angularVelocity);
        this.root.setAngularVelocity(newAngularVelocity);

        var impulse = linearVelocity.clone().negate();
        this.root.applyCentralImpulse(impulse);

        var getForceVector = new THREE.Vector3(0,0, -1*this.botOptions.thrustImpulse).applyMatrix4(rotationMatrix);
        this.root.applyCentralImpulse(getForceVector);

        this.lastTime = now;

        //////////////////////////////
        ///////  FIRING LOGIC ////////
        //////////////////////////////

        if ( Math.abs(vTarget2D.x) <= 0.15 && Math.abs(vTarget2D.y) <= 0.15 && vTarget2D.z < 1 && closestDistance < maxDistance) {
            this.botFire('turret');
        }

    }
});

