s.Color = new Class({

	toString: "color",

	construct: function( options ){

		//upon construction we set the color's properties to the passed in options'
		this.game = options.game;

		this.red = options.red;

		this.green = options.green;

		this.blue = options.blue;

		this.alpha = options.alpha;


		this.color = "rgba(" + this.red + "," + this.green + "," + this.blue + "," + this.alpha+ ")";


		//storing the initial color, as the classes' color is dynamically animated later on and needs to be reset to initialization parameters

		this.startingColor = {

			red: this.red,

			green: this.green,

			blue: this.blue,

			alpha: this.alpha,

			color: "rgba(" + this.red + "," + this.green + "," + this.blue + "," + this.alpha+ ")"

		};

		//pseudo-global memory of how many frames the animation has been playing for

		this.timesCounted = 0;

	},

	animate: function( options ){


		//animate starts up an asynchronous loop for changeColor and passes in some custom options


		//clears all ongoing animations

		if ( this.color !== this.startingColor.color ){

			this.game.unhook(this.changeColor);

		}

		
		//reinitializes the frame count

		this.timesCounted = 0;


		//if we're animating to a new color, instead of animating back to the initial one, we reset the colors

		if ( !options.fading ){

			this.red = this.startingColor.red;

			this.green = this.startingColor.green;

			this.blue = this.startingColor.blue;

			this.alpha = this.startingColor.alpha;

			this.color = this.startingColor.color;

		}


		//initializing the options we intend to pass into change color


		this.endColor = options.color;

		this.frames = options.frames;

		var frames = this.frames;

		var endColor = this.endColor;

		this.redStep = Math.abs( ~~ ( ( endColor.red - this.red ) / frames ) );

		this.greenStep = Math.abs( ~~ ( ( endColor.green - this.green ) / frames ) );

		this.blueStep = Math.abs( ~~ ( ( endColor.blue - this.blue ) / frames ) );

		this.alphaStep = Math.abs( ~~ ( ( ( endColor.alpha * 100 ) - ( this.alpha * 100 ) ) / frames ) ) / 100;

		this.changeColor = this.changeColor.bind( this );


		//calls changeColor every frame for the set number of frames;

        this.game.hook( this.changeColor );

	},

	changeColor: function( ){


		//changeColor is repetitively called by animate and changes this classes color to a target one in steps

		var endColor = this.endColor;


		//if we've reached the target amount of frames we clear animatons and then recursively call animate to change the color back to it's initial state

		if ( this.timesCounted === this.frames ){

			this.game.unhook( this.changeColor) ;

			this.red = endColor.red;

			this.green = endColor.green;

			this.blue = endColor.blue;

			this.alpha = endColor.alpha;

			this.color = "rgba(" + this.red + "," + this.green + "," + this.blue + "," + this.alpha+ ")";


			if ( this.color !== this.startingColor.color ){

				this.animate({

					color:{

						red: this.startingColor.red,

						green: this.startingColor.green,

						blue: this.startingColor.blue,

						alpha: this.startingColor.alpha

					},

					frames: this.frames,

					fading: true

				});
			}

		} else {

		var frames = this.frames,

		redStep = this.redStep,

		greenStep = this.greenStep,

		blueStep = this.blueStep,

		alphaStep = this.alphaStep;


		if( this.red < endColor.red ){

			this.red += redStep;	

		} 
		else if ( this.red > endColor.red ) {

			this.red -= redStep;

		}

		if( this.green < endColor.green ){

			this.green += greenStep;

		} 
		else if ( this.green > endColor.green ) {

			this.green -= greenStep;

		}


		if( this.blue < endColor.blue ){

			this.blue += blueStep;	

		} 
		else if ( this.blue > endColor.blue ) {

			this.blue -= blueStep;

		}

		if( this.alpha < endColor.alpha ){

			this.alpha += alphaStep;	

		} 
		else if ( this.alpha > endColor.alpha ) {

			this.alpha -= alphaStep;

		}


		this.color = "rgba(" + this.red + "," + this.green + "," + this.blue + "," + this.alpha + ")";


		this.timesCounted ++;

		}
	}
});
s.Keyboard = new Class({
  toString: 'Keyboard',

  keys: {
    'left'    : 37,
    'up'      : 38,
    'right'   : 39,
    'down'    : 40,
    'space'   : 32,
    'pageup'  : 33,
    'pagedown': 34,
    'tab'     : 9,
    'w'       : 87,
    'a'       : 65,
    's'       : 83,
    'd'       : 68,
    'backtick': 192,
    'shift'   : 16,
    'tilde'   : 192
  },

  modifiers: ['shift', 'ctrl', 'alt', 'meta'],

  construct: function(game, player) {
    this.keyCodes = {};

    // Listen to key events
    window.addEventListener('keydown', this.handleKeyChange.bind(this, true), false);
    window.addEventListener('keyup', this.handleKeyChange.bind(this, false), false);
  },

  destruct: function() {
    window.removeEventListener('keydown', this.handleKeyChange, false);
    window.removeEventListener('keyup', this.handleKeyChange, false);
  },

  handleKeyChange: function(pressed, e) {
    var keyCode = e.keyCode;
    this.keyCodes[keyCode] = pressed;

    // update this.modifiers
    this.modifiers.shift = e.shiftKey;
    this.modifiers.ctrl = e.ctrlKey;
    this.modifiers.alt = e.altKey;
    this.modifiers.meta = e.metaKey;
  },

  pressed: function(keyDesc) {
    var keys = keyDesc.split("+");
    for (var i = 0; i < keys.length; i++) {
      var key = keys[i];
      var pressed;
      if (this.modifiers.indexOf(key) !== -1) {
        pressed = this.modifiers[key];
      }
      else if (Object.keys(this.keys).indexOf(key) != -1) {
        pressed = this.keyCodes[this.keys[key]];
      }
      else {
        pressed = this.keyCodes[key.toUpperCase().charCodeAt(0)];
      }

      if(!pressed) return false;
    }

    return true;
  }
});


s.Mouse = new Class({
  toString: 'Mouse',

  construct: function (mouseType, options) {
    // ensure that s.Mouse.update always runs in the context of the mouse
    this.update = this.update.bind(this);
    this.oculusMouse = this.oculusMouse.bind(this);
    this.keyboardMouse = this.keyboardMouse.bind(this);
    this.noMouse = this.noMouse.bind(this);

    this.mouseType = mouseType;
    this.mice = {
      'oculus'  : this.oculusMouse,
      'keyboard': this.keyboardMouse,
      'none'    : this.noMouse
    };

    // Store references to game objects from s.Controls
    this.HUD = options.HUD;
    this.game = options.game;
    this.player = options.player;
    this.camera = options.camera;
  },

  update: function () {
    return this.mice[this.mouseType].apply(this, arguments);
  },

  keyboardMouse: function (movables) {
    // Set yaw to zero if cursor is inside the crosshair region
    if (this.HUD.targetX > movables.centerX - movables.crosshairs.width/2 &&
        this.HUD.targetX < movables.centerX + movables.crosshairs.width/2){
      movables.yaw = 0;
    } else {
      // X scales yaw
      var yawDivisor = this.HUD.targetX < movables.centerX ?
        (movables.centerX-movables.radius)/((movables.centerX-this.HUD.targetX)*4) :
        -(movables.centerX+movables.radius)/((-movables.centerX+this.HUD.targetX)*4);
      movables.yaw = movables.yawSpeed/yawDivisor/movables.thrustScalar;
    }

    // Set pitch to zero if cursor is inside the crosshair region
    if (this.HUD.targetY > movables.centerY - movables.crosshairs.height/2 && this.HUD.targetY < movables.centerY + movables.crosshairs.height/2){
      movables.pitch = 0;
    } else {
      // Y scales pitch
      var pitchDivisor = this.HUD.targetY < movables.centerY ?
        (movables.centerY+movables.radius)/((movables.centerY-this.HUD.targetY)*4) : -(movables.centerY+movables.radius)/((-movables.centerY+this.HUD.targetY)*4);
      movables.pitch = movables.pitchSpeed/pitchDivisor/movables.thrustScalar;
    }
    return {yaw: movables.yaw, pitch: movables.pitch};
  },

  oculusMouse: function (movables) {
    var brakes;
    var thrust;
    // TODO: X makes the oculus view look left or right, leaving the ship fixed

    // Y makes the ship throttle or break;
    if (this.HUD.targetY > movables.centerY){
      // mouse is below center
      brakes = 1;
    } else if (this.HUD.targetY < movables.centerY){
      // mouse is above center
      thrust = 1;
    }
    return {brakes: brakes, thrust: thrust};
  },

  noMouse: function (movables) {
    this.HUD.targetX = movables.centerX;
    this.HUD.targetY = movables.centerY;
    return {}; // for s.Control Ln:120 and 121;
  },

});

